import { useState } from 'react'
import '../App.css'

export function ContactMe() {
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [inquiries, setInquiries] = useState("")
    const [textarea, setTextarea] = useState("")

    const handleSubmit = (e) => {
        e.preventDefault()
        if (name && email & inquiries) {
        alert(`Thank you for your submission ${name} , we will get back to you shortly`)
        } else {
        alert(`Please provide all the necessary details`)
        }
    }

    return (
        <div className="contact-me">

            <form onSubmit={handleSubmit}>
                <label>Name</label>
                <input type="name" onChange={(e)=> setName(e.target.value)}></input>
                <label>Email Address</label>
                <input type="email"></input>
                <label>Type of inquiry</label>
                <input list="inquiries"></input>
                <datalist id="inquiries" placeholder="Choose or type your inquiry">
                    <option>Freelance</option>
                    <option>Full-time</option>
                    <option>Part-time</option>
                </datalist>
                <label>Your Message</label>
                <textarea rows="10" cols="30"></textarea>
                <button>Submit</button>
            </form>
        </div>
    )
}
