import { useState } from 'react'
import '../App.css'

export function ContactMe() {
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [inquiries, setInquiries] = useState("")
    const [textarea, setTextarea] = useState("")

    const handleSubmit = (e) => {
        e.preventDefault()
        //const errors = {name: "hi", person:"Aaron"}
        //if (!name) {alert(`${errors.name} ${errors.person}`)}
        let validEmail = false
        if (!/\S+@\S+\.\S+/.test(email)) {validEmail = true}
        if (name && email && validEmail && inquiries && textarea.toString > 10) {
        alert(`Thank you for your submission ${name} , we will get back to you shortly`)
        setName("")
        setEmail("")
        setInquiries("")
        setTextarea("")
        } else {
        alert(`Please provide all the necessary details`)
        }
    }

    return (
        <div className="contact-me">

            <form onSubmit={handleSubmit}>
                <label>Name</label>
                <input type="name" onChange={(e)=> setName(e.target.value)} value={name}></input>
                <label>Email Address</label>
                <input type="email" onChange={(e)=>setEmail(e.target.value)} value={email}></input>
                <label>Type of inquiry</label>
                <input list="inquiries" onChange={(e)=>setInquiries(e.target.value)} value={inquiries}></input>
                <datalist id="inquiries" placeholder="Choose or type your inquiry">
                    <option>Freelance</option>
                    <option>Full-time</option>
                    <option>Part-time</option>
                </datalist>
                <label>Your Message</label>
                <textarea rows="10" cols="30" onChange={(e)=> setTextarea(e.target.value)} value={textarea}></textarea>
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
