import { forwardRef } from 'react';
import '../App.css';
import { littleLemon, toDoList, simpleCalculator } from '../assets/project';

// Use forwardRef to handle ref forwarding
export const ProjectSection = forwardRef((props, ref) => {
    return (
        <div className='projects' ref={ref} id={props.id}>
            <h2>My Projects</h2>
            <div className="project-section">
                <div className="card">
                    <img src={littleLemon} alt="Little Lemon website" />
                    <h3>Little Lemon Website</h3>
                    <p>A beautifully designed website for a fictional restaurant named Little Lemon. It features a modern layout with an emphasis on vibrant visuals and a user-friendly menu. The site includes sections for the menu, special offers, and contact information, making it perfect for attracting and informing customers.</p>
                </div>
                <div className="card">
                    <img src={toDoList} alt="a to do list app" />
                    <h3>To Do List App</h3>
                    <p>A simple and intuitive to-do list application that helps users manage their tasks efficiently. Users can add, edit, and remove tasks, and mark them as completed. The app provides a clean interface and basic functionality to keep track of daily activities and stay organized.</p>
                </div>
                <div className="card">
                    <img src={simpleCalculator} alt="a simple calculator" />
                    <h3>Simple Calculator</h3>
                    <p>A basic calculator application designed to perform fundamental arithmetic operations such as addition, subtraction, multiplication, and division. It features a straightforward interface, making it easy for users to perform quick calculations without any distractions.</p>
                </div>
            </div>
        </div>
    );
});
